      function textToBase64Barcode(text){
        var canvas = document.createElement("canvas");
        JsBarcode(canvas, text, {
          format: "CODE128",
          displayValue: "false",
          background: "#E6F0FF"
          });
        return canvas.toDataURL("image/png");
      }
